package main

import (
	"github.com/injoyai/io"
	"github.com/injoyai/io/dial"
	"github.com/injoyai/io/listen"
	"github.com/injoyai/logs"
	"net/http"
	_ "net/http/pprof"
	"time"
)

func main() {

	go func() {
		http.ListenAndServe(":6060", nil)
	}()

	go listen.RunTCPServer(12000, func(s *io.Server) {
		s.Debug(false)
		s.Logger.SetLevel(io.LevelError)
		go func() {
			for {
				<-time.After(time.Second * 5)
				logs.Debug(s.GetClientLen())
			}
		}()
	})

	for i := 0; i < 10000; i++ {
		<-time.After(time.Millisecond * 1)
		go dial.NewTCP("127.0.0.1:12000", func(c *io.Client) {
			//closeTime := time.Now().Add(time.Second * time.Duration(rand.Intn(10)+10))
			c.Debug(false)
			c.Logger.SetLevel(io.LevelError)
			go c.Run()
			c.GoTimerWriter(time.Second*2, func(w *io.IWriter) error {
				//if time.Since(closeTime) < 0 {
				//	return c.Close()
				//}
				_, err := w.WriteString("")
				return err
			})

		})
	}
	select {}
}
